mapper.select:
SELECT IFNULL(MAX(RDCNT),0)+1 AS RDCNT
  FROM LETTNBBS
 WHERE BBS_ID = #{bbsId}
   AND NTT_ID = #{nttId}

mapper.update:

 UPDATE LETTNBBS SET 
  RDCNT = #{inqireCo}
, LAST_UPDUSR_ID = 'USRCNFRM_00000000000'
, LAST_UPDT_PNTTM = SYSDATE()
  WHERE BBS_ID = #{bbsId}
    AND NTT_ID = #{nttId}

UPDATE LETTNBBS
   SET NTT_SJ = #{nttSj}
     , NTT_CN = #{nttCn}
     , NTCR_ID = #{ntcrId}
     , NTCR_NM = #{ntcrNm}
     , PASSWORD = #{password}
     , NTCE_BGNDE = #{ntceBgnde}
     , NTCE_ENDDE = #{ntceEndde}
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , ATCH_FILE_ID = #{atchFileId}
     , LAST_UPDT_PNTTM = SYSDATE()
 WHERE BBS_ID = #{bbsId}
   AND NTT_ID = #{nttId}

